import { CHANGE_SIGNAL, MOVE_CAR } from '../actions';

const INITIAL_STATE = {
  color: 'red',
};

export const reducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case CHANGE_SIGNAL:
    return {
      color: action.payload,
    };
  default:
    return state
  }
}

const initialState = {
  cars: {
    red: false,
    blue: false,
    yellow: false,
  },
};

export const reducerCars = (state = initialState, action) => {
  switch (action.type) {
    case MOVE_CAR:
      return { ...state, cars: { ...state.cars, [action.car]: action.side } };
    default:
      return state;
  }
}
