import { CHANGE_SIGNAL, MOVE_CAR } from '../actions';
import { combineReducers } from 'redux';

const INITIAL_STATE = {
  color: 'red',
};

const reducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case CHANGE_SIGNAL:
    return {
      color: action.payload,
    };
  default:
    return state
  }
}

const initialState = {
  cars: {
    red: false,
    blue: false,
    yellow: false,
  },
};

const reducerCars = (state = initialState, action) => {
  switch (action.type) {
    case MOVE_CAR:
      return { ...state, cars: { ...state.cars, [action.car]: action.side } };
    default:
      return state;
  }
}

const rootReducer = combineReducers(reducer, reducerCars);

export default rootReducer;
