const INITIAL_STATE = {
  signal: {
    color: 'red',
  },
};

const reducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case 'red':
    return {
      ...state,
      signal: { ...state.signal, color: 'red'},
    };
  case 'yellow':
    return {
      ...state,
      signal: { ...state.signal, color: 'yellow'},
    };
  case 'green':
    return {
      ...state,
      signal: { ...state.signal, color: 'green'},
    };
  default:
    return state
  }
}

export default reducer;
